mapper.select:
select 

from course_chapter_video
where chapter_id = #{chapterId,jdbcType=INTEGER}
  and course_id = #{courseId,jdbcType=INTEGER}
  and video_id = #{videoId,jdbcType=INTEGER}

mapper.delete:
delete from course_chapter_video
where chapter_id = #{chapterId,jdbcType=INTEGER}
  and course_id = #{courseId,jdbcType=INTEGER}
  and video_id = #{videoId,jdbcType=INTEGER}

mapper.update:
update course_chapter_video


    video_url = #{videoUrl,jdbcType=VARCHAR},


    video_name = #{videoName,jdbcType=VARCHAR},


where chapter_id = #{chapterId,jdbcType=INTEGER}
  and course_id = #{courseId,jdbcType=INTEGER}
  and video_id = #{videoId,jdbcType=INTEGER}

update course_chapter_video
set video_url = #{videoUrl,jdbcType=VARCHAR},
  video_name = #{videoName,jdbcType=VARCHAR}
where chapter_id = #{chapterId,jdbcType=INTEGER}
  and course_id = #{courseId,jdbcType=INTEGER}
  and video_id = #{videoId,jdbcType=INTEGER}
